import { FETCH_DATA_SUCCESS } from '../actionType';

export const initialState = {
  weather: {},
};

export const appReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        weather: payload,
      };
    default:
      return state;
  }
};
