import axios from 'axios';
import { setRepos } from '../../reducers/reposReducer';

export const getRepos = (searchQuery = "London") => {
    return async (dispatch) => {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${searchQuery}&appid=d6487c851d4294ca86263fcb2861f71f`)
        // const response = await axios.get(`https://api.github.com/search/repositories?q=${searchQuery}&sort=stars`)
        dispatch(setRepos(response.data))
    }
}

