import styled from 'styled-components';


export const MainWrapper = styled.div`
  position: relative;
  .imgWrapper {
    position: absolute;
    top: 25px;
    right: 20px;
  }
`

export const Input = styled.input`
  width: 400px;
  height: 80px;
  font-size: 32px;
  padding: 12px 32px;
  border: none;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
  border-radius: 20px;
  background: white;
  outline: none;
`;
