import React from 'react';
import { useSelector } from 'react-redux';

const Error = () => {
  const state = useSelector((state) => state);
  const { isLoading, error } = state;
  return (
    <div>
        <h2>Loading please wait...</h2> 
        : error ? (
        <h1>{error?.message}</h1>
        <h3>{error && error.message}</h3>
      )
    </div>
  );
};

export default Error;
