import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { changeTheme, fetchData } from '../store/thunk/action/app';

import Header from './header/Header';
import Main from './main/Main';

import { AppWrapper, MainAppWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData('London'));
  }, [dispatch]);

  const theme = useSelector((state) => state.app.theme);

  const onChangeTheme = () => {
    dispatch(changeTheme());
  };

  return (
    <AppWrapper changeTheme={theme}>
      <MainAppWrapper>
        <button onClick={onChangeTheme}>thunk</button>
        <Header />
        <Main />
        <GlobalStyle />
      </MainAppWrapper>
    </AppWrapper>
  );
};

export default App;
