import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';

import { changeThemeThunk, fetchData } from '../store/thunk/action/app';

import Header from './header/Header';
import Main from './main/Main';

import { AppWrapper, MainAppWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {
  // const [theme, setTheme] = useState(false)
  // const changeTheme = () => {
  //   setTheme(!theme)
  // }
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData('London'));
  }, [dispatch]);

  return (
    <AppWrapper >
      <MainAppWrapper>
        {/* <button onClick={changeTheme}>click</button> */}
        <button onClick={() => dispatch(changeThemeThunk())}>thunk</button>
        <Header />
        <Main />
        <GlobalStyle />
      </MainAppWrapper>

    </AppWrapper>
  );
};

export default App;
