const SET_REPOS = 'SET_REPOS';
const SET_TEST = 'SET_TEST'

const defaultState = {
  items: [],
  isFetching: true,
};

function reposReducer(state = defaultState, action) {
  switch (action.type) {
    case SET_TEST:
      return {
        ...state,
        items: action.payload.data,
      };
    default:
      return state;
  }
}

export const setRepos = (repos) => ({ type: SET_TEST, payload: repos });


export default reposReducer;
