import styled from 'styled-components';

export const MainCardWrapper = styled.div`
  margin-top: 30px;
  max-width: 400px;
  width: 100%;
  border: none;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
  border-radius: 20px;
  padding: 12px 32px;
`;

export const Wrapper = styled.div`
  display: flex;
  flex-direction: column;

  .cardInfo {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
`;
