import { createAsyncThunk, createReducer, createSlice } from "@reduxjs/toolkit";
import { languegeObject } from "../../data/languageObject";


const languageSlice = createReducer({
  name: 'language',
  initialState: {
    obj: languegeObject
  },
  extraReducers: (builder) => {
    //fulfilled
    builder.addCase( (state, action) => {
      state.language = action?.payload;
    });
    //rejected
    builder.addCase((state, action) => {
      state.language = undefined;
    });
  },
});

export default languageSlice.reducer;

const todosReducer = createReducer([], (builder) => {
  builder
    .addCase('ADD_TODO', (state, action) => {
      // "мутируем" массив, вызывая push()
      state.push(action.payload);
    })
    .addCase('TOGGLE_TODO', (state, action) => {
      const todo = state[action.payload.index];
      // "мутируем" объект, перезаписывая его поле `completed`
      todo.completed = !todo.completed;
    })
    .addCase('REMOVE_TODO', (state, action) => {
      // мы по-прежнему можем использовать иммутабельную логику обновления состояния
      return state.filter(
        (todo, i) => i !== action.payload.index
      );
    });
});
