import { FETCH_DATA_SUCCESS } from '../actionType';

export const initialState = {
  weather: {
    main: {
      temp: '',
      country: '',
    },
    weather: [{
      0: {
        description: ''
      }
    }],
    wind: {
      speed
    },
    sys: {
      country: ''
    }
  },
  lang: 'eng'
};

export const appReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        weather: payload,
      };
      case 'APP_CHANGE_LANG':
        return {
          ...state,
          lang: payload,
        }
    default:
      return state;
  }
};
