const SET_REPOS = 'SET_REPOS';

const defaultState = {
  data: [],
  isFetching: true,
};

function reposReducer(state = defaultState, action) {
  switch (action.type) {
    case SET_REPOS:
      return {
        ...state,
        items: action.payload.data,
      };
    default:
      return state;
  }
}

export const setRepos = (repos) => ({ type: SET_REPOS, payload: repos });

export default reposReducer;
