import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { fetchData } from '../../store/thunk/action/app';
import { languegeObject } from '../../store/data/languageObject';

import { SearchWrapper } from './styledComponents/SearchInput';

import cities from 'cities.json';


const options = cities.map(({ name, ...n }) => ({
  value: name,
  label: name,
  ...n,
}));
console.log('options', options);
const SearchInput = () => {
  const [value, setValue] = useState('');
  const [isOpen, setIsOpen] = useState(true);

  const dispatch = useDispatch();
  const state = useSelector((state) => state.app);

  const { lang } = state;

  const onKeyPressHandler = (e) => {
    if (e.key === 'Enter') {
      dispatch(fetchData(e.target.value));
    }
  };

  const filteredCountries = cities.filter((country) => {
    return country.name.toLowerCase().includes(value.toLowerCase());
  });


  const itemClickHandler = (e) => {
    setValue(e.target.textContent);
    setIsOpen(!isOpen);
  };

  const inputClickHandler = () => {
    setIsOpen(true);
  };

  // test



  return (
    <SearchWrapper>
      <input
        className="searchInput"
        value={value}
        placeholder={languegeObject[lang].placeholder}
        onKeyPress={onKeyPressHandler}
        onChange={(e) => setValue(e.target.value)}
        onClick={inputClickHandler}
      />
      <ul className="autocomplete">
        {value && isOpen
          ? filteredCountries.map((country, index) => {
              return (
                <li
                  key={index}
                  className="autocompelete_item"
                  onClick={itemClickHandler}
                >
                  {country.name}
                </li>
              );
            })
          : null}
      </ul>
    </SearchWrapper>
  );
};

export default SearchInput;
