import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';

import { fetchWeatherAction } from '../../../../store/redux/slices/weatherSlices';
import { fetchData } from '../../../../store/thunk/action/app';

import { Input } from './styledComponents/SearchInput';

const SearchInput = () => {
  const { t } = useTranslation();

  const dispatch = useDispatch();
  const lang = useSelector(state => state.app.lang)

  // const onKeyPressHandler = (e) => {
  //   if (e.key === 'Enter') {
  //     dispatch(fetchWeatherAction(e.target.value));
  //   }
  // };
  const onKeyPressHandler = (e) => {
    if (e.key === 'Enter') {
      dispatch(fetchData(e.target.value));
    }
  };

  return (
    <Input
      type="text"
      placeholder={t('placeholder')}
      name="search_city"
      onKeyPress={onKeyPressHandler}
    />
  );
};

export default SearchInput;
