import React, { useEffect, useState } from 'react';
import axios from 'axios';

import SearchInput from './components/searchInput/SearchInput';

import GlobalStyle from '../styledComponents/GlobalStyle';
import { MainWrapper } from './styledComponents/App';

const API_KEY = 'd6487c851d4294ca86263fcb2861f71f';

const App = () => {
  const [data, setData] = useState([])
  const [value, setValue] = useState('');

  const getStatistic = () => {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=London&appid=${API_KEY}`)
      .then(res => {
        const countries = res.data.name;
        setData(countries);
      })
      .catch(error => {
        return error;
      });
  }

  useEffect(() => {
    getStatistic()
  }, [])

  const filteredData = data.filter(items => {
    return items.Country.toLowerCase().includes(value.toLowerCase());
  });

  return (
    <MainWrapper>
      <SearchInput setValue={setValue} value={value} />
      <GlobalStyle />
    </MainWrapper>
  );
}

export default App;
