const SET_REPOS = 'SET_REPOS'

const defaultState = {
  data: [],
  isFetchimg: true,
}

export default function reposReducer(state = defaultState, action) {
  switch(action.type) {
    case SET_REPOS: 
      return {
        ...state,
        data: action.payload.data
      }
    default: 
      return state
  }
}

export const setRepos = (repos) => ({type: SET_REPOS, payload: repos})
