import styled from 'styled-components';

export const light = {
  color: 'red'
}

export const AppWrapper = styled.div`
  height: 100vh;
  transition: all 0.2s ease-in;
  background: ${(props) => (props.changeTheme ? '#000' : 'white')};
`;

export const MainAppWrapper = styled.div`
  max-width: 1140px;
  margin: 0 auto;
`;
