import styled from 'styled-components';

export const BtnWrapper = styled.div`
  display: flex;

  img {
    position: relative;
    width: 30px;
    margin-left: 15px;
    cursor: pointer;
    transition: color .2s linear;
    ::after {
      content: '';
      display: block;
      height: 3px;
      background: red;
      position: absolute;
      top: 100%;
      left: 0;
      z-index: 1;
    }
    :hover {
      color: red;
    }
  }
`;
