import styled from 'styled-components';

import { Gray, White } from '../../../../../assets/colors/colors';

export const BtnWrapper = styled.div`
  display: flex;

  .btn {
    font-size: 20px;
    padding: 10px 30px;
    margin-left: 15px;
    border: none;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    background: ${White};
    color: ${Gray};
    outline: none;
    :hover {
      background: red;
    }
  }
`;
