import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';

import { fetchData } from '../store/thunk/action/app';

import Header from './header/Header';
import Main from './main/Main';

import { MainAppWrapper, Wrapper } from './styledComponents/App';
import GlobalStyle, { lightTheme } from '../styledComponents/GlobalStyle';

const App = () => {
  const [theme, setTheme] = useState('light');

  const changeTheme = () => {
    theme === 'light' ? setTheme('dark') : setTheme('light')
  };
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData('London'));
  }, [dispatch]);

  return (
    <Wrapper theme={theme === 'light' ? lightTheme : dar}>
      <MainAppWrapper>
        <button onClick={changeTheme}>change</button>
        <Header />
        <Main />
        <GlobalStyle />
      </MainAppWrapper>
    </Wrapper>
  );
};

export default App;
