import React, { useContext } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import weatherIcon from '../../../assets/image/weatherIcon.png';

import { ThemeContext } from '../../App';

import CustomButton from './customButton/CustomButton';
import SearchInput from './searchInput/SearchInput';

import { HeaderWrapper } from './styledComponents/Header';

const Header = () => {
  const dispatch = useDispatch();

  const state = useSelector((state) => state);
  const { darkTheme } = state;
  const toggleTheme = () => {
    dispatch(prevDarkTheme => !prevDarkTheme)
  }

  // const darkTheme = useContext(ThemeContext);

  const themeStyles = {
    backgroundColor: darkTheme ? '#333' : '#CCC',
    color: darkTheme ? '#CCC' : '#333',
    padding: '2rem',
    margin: '2rem',
  };

  return (
    <HeaderWrapper style={themeStyles}>
      <div className="weatherHeaderItems">
        <img src={weatherIcon} alt="weatherIcon" />
        <h1>Weather App</h1>
      </div>
      <div className="weatherItems">
        <SearchInput />
        <CustomButton />
      </div>
      <button onClick={toggleTheme} >Toggle Theme</button>

    </HeaderWrapper>
  );
};

export default Header;
