import React, { useEffect, useState } from 'react';
import axios from 'axios';

import SearchInput from './components/searchInput/SearchInput';

import GlobalStyle from '../styledComponents/GlobalStyle';
import { MainWrapper } from './styledComponents/App';

const API_KEY = 'd6487c851d4294ca86263fcb2861f71f';

const App = () => {
  const [data, setData] = useState([]);
  const [value, setValue] = useState('');

  const getWeather = () => {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=Kyiv&appid=${API_KEY}`)
      .then(res => {
        const weatherData = res.data;
        console.log(weatherData);
        setData(weatherData);
      })
      .catch(error => {
        return error;
      });
  }

  useEffect(() => {
    getWeather()
  }, [])

  const filteredData = data.filter(items => {
    // console.log(items);
    return items.name.toLowerCase().includes(value.toLowerCase());
  });

  console.log(filteredData);

  // const getWeather = async (e) => {
