import { languegeObject } from '../../data/languageObject';
import { FETCH_DATA_SUCCESS } from '../actionType';


export const langReducer = (state = languegeObject, action) => {
  const { type, payload } = action;
  switch (type) {
    case FETCH_DATA_SUCCESS:
      return {
        ...state,
        // weather: payload,
      };
    default:
      return state;
  }
};
