import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchWeatherAction } from '../../../redux/slices/weatherSlices';
import { MainCardWrapper, Wrapper } from './styledComponents/ThisDayWeather';

const WeatherItems = () => {
  const timeNow = new Date();
  const test = timeNow.getHours()+ ':' + timeNow.getMinutes()+ ':' + timeNow.getSeconds()
  console.log(test);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchWeatherAction('Kyiv'));
  }, [dispatch]);

  // select state from store
  const state = useSelector((state) => state);
  const { weather, isLoading, error } = state;
  return (
    <MainCardWrapper>
      {isLoading ? (
        <h2>Loading please wait...</h2>
      ) : error ? (
        // <h1>{error?.message}</h1>
        <h3>{error && error.message}</h3>
      ) : (
        <div>
          <div>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
              <h1>
                {Math.floor(weather?.main.temp)}
                <sup>&deg;</sup>
              </h1>
              <img
                src={`https://openweathermap.org/img/wn/${weather?.weather[0].icon}@2x.png`}
                alt={weather?.weather[0].description}
              />
            </div>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
              <p>Today</p>
              <p>{weather?.weather[0].description}</p>
            </div>
          </div>
          <div>
            {/* <p>Time: {Date.getTimezoneOffset()}</p> */}
            <span>City: {weather?.name}</span>
            <sup>{weather?.sys.country}</sup>
          </div>
        </div>
      )}
    </MainCardWrapper>
  );
};

export default WeatherItems;
