import styled from 'styled-components';

import {
  Black,
  DarkGray,
  DodgerBlue,
  Gray,
  LightBlack,
  White,
} from '../../../../assets/colors/colors';

export const MainDailyWeatherWrapper = styled.div`
  max-width: 400px;
  width: 100%;
  border: none;
  box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.25);
  border-radius: 20px;
  padding: 15px 30px;
  height: 200px;
  transition: background 0.3s ease-in;
  background: ${(props) => (props.changeTheme ? `${LightBlack}` : `${White}`)};

  .dailyWeatherWrapper {
    display: flex;
    flex-direction: column;

    .dailyWeatherCardInfo {
      display: flex;
      justify-content: space-between;
      align-items: center;

      h1 {
        color: ${DodgerBlue};
      }

      p {
        font-size: 25px;
      }

      h3 {
        color: ${(props) => (props.changeTheme ? `${White}` : `${Black}`)};
      }
    }
    .dailyWeatherItem {
      display: flex;
      align-items: center;
      color: ${(props) => (props.changeTheme ? `${White}` : `${Black}`)};


      p {
        color: ${(props) => (props.changeTheme ? `${DarkGray}` : `${Gray}`)};
        margin-right: 5px;
      }

      span {
        color: ${(props) => (props.changeTheme ? `${White}` : `${Black}`)};
      }

      sup {
        /* color: ${(props) => (props.changeTheme ? `${White}` : `${Black}`)}; */
        margin-left: 3px;
        font-weight: bold;
        font-size: 13px;
      }
    }
  }
`;
