import styled from "styled-components";

export const MainDetailsDailyWeatherWrapper = styled.div`
  position: relative;
  max-width: 700px;
  width: 100%;
  border: none;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
  border-radius: 20px;
  padding: 15px 32px;

  .detailsDailyItems {
    display: flex;
    align-items: center;

    .imageCircle {
      display: flex;
      width: 50px;
      height: 50px;
      border: 1px solid black;
      border-radius: 50%;
    }

    img {
      width: 35px;
    }

    p {
      margin-left: 5px;
    }
  }

  .cloud_image {
    position: absolute;
    top: 0;
    right: 0;
  }
`;
