import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { changeThemeThunk, fetchData } from '../store/thunk/action/app';

import Header from './header/Header';
import Main from './main/Main';

import { AppWrapper, MainAppWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {
  const [theme, setTheme] = useState(false)

  const changeTheme = () => {
    setTheme(!theme)
  }

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData('London'));
  }, [dispatch]);

  const themetest = useSelector((state) => state.app.theme);

  const changeThemeTest = () => {
    dispatch(changeThemeThunk())
  }

  return (
    <AppWrapper test={theme}>
      <MainAppWrapper>
        <button onClick={changeTheme}>click</button>
        <button onClick={changeThemeTest}>thunk</button>
        <Header />
        <Main />
        <GlobalStyle />
      </MainAppWrapper>

    </AppWrapper>
  );
};

export default App;
