import { FETCH_DATA_ERROR, FETCH_DATA_START, FETCH_DATA_SUCCESS } from '../actionType';

import ApiWeather from '../Api';

const fetchDataStart = () => {
  return { type: FETCH_DATA_START };
};

const fetchDataSuccess = (payload) => {
  return { type: FETCH_DATA_SUCCESS, payload };
};
const fetchDataError = () => {
  return { type: FETCH_DATA_ERROR };
};

export const fetchData = (pa) => {
  return async (dispatch) => {
    try {
      dispatch(fetchDataStart());
      const data = await ApiWeather.fetchData();
      dispatch(fetchDataSuccess(data));
    } catch (e) {
      dispatch(fetchDataError());
    }
  };
};
