import styled from 'styled-components';

import { White } from '../../../assets/colors/colors';

export const FormWrapper = styled.div`
  position: relative;

  .autocomplete {
    position: absolute;
    left: 0;
    top: 50px;
    width: 100%;
    background: white;
    list-style: none;
    margin: 0;
    padding: 0;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    max-height: 240px;
    height: auto;
    overflow: auto;

    .autocomplete_item {
      padding: 10px;
      :hover {
        background: rgb(180, 180, 180);
        cursor: pointer;
        transition: cubic-bezier(1, 0, 0, 1) .2s all;
      }
    }
  }
`;

export const Input = styled.input`
  max-width: 420px;
  width: 100%;
  height: 40px;
  font-size: 22px;
  padding: 12px 32px;
  border: none;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.25);
  border-radius: 20px;
  background: ${White};
  outline: none;
`;
