import axios from 'axios';
import { setRepos } from '../store/reposReducer';

// const URL = 'https://api.openweathermap.org/data/2.5/weather';
const API_KEY = 'd6487c851d4294ca86263fcb2861f71f';

export const getRepos = async (searchQuery = 'London') => {
  return async(dispatch) => {
    const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${searchQuery}&units=metric&appid=${API_KEY}`)
    dispatch(setRepos(response.data))
  }
};
