import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { fetchData } from '../../store/thunk/action/app';
import { languegeObject } from '../../store/data/languageObject';

import { FormWrapper, Input } from './styledComponents/SearchInput';

const SearchInput = () => {
  const [value, setValue] = useState('');
  const dispatch = useDispatch();

  const state = useSelector((state) => state.app);

  const { weather, lang } = state;

  const onKeyPressHandler = (e) => {
    if (e.key === 'Enter') {
      dispatch(fetchData(e.target.value));
    }
  };

  const onChangeHandler = (e) => {
    setValue(e.target.textContent)
  }

  return (
    <FormWrapper>
      <Input
        type="text"
        placeholder={languegeObject[lang].placeholder}
        name="search_city"
        onKeyPress={onKeyPressHandler}
      />
      <ul className="autocomplete">
        <li className="autocomplete_item" onC>{weather.name}</li>
      </ul>
    </FormWrapper>
  );
};

export default SearchInput;
