import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { changeThemeThunk, fetchData } from '../store/thunk/action/app';

import Header from './header/Header';
import Main from './main/Main';

import { AppWrapper, MainAppWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData('London'));
  }, [dispatch]);

  const themeTest = useSelector((state) => state.app.theme);
  console.log('themeTest', themeTest);

  const changeThemeTest = () => {
    dispatch(changeThemeThunk())
  }

  return (
    <AppWrapper test={themeTest}>
      {themeTest ? <h1>hello</h1> : <h2>test</h2>}
      <MainAppWrapper>
        <button onClick={changeThemeTest}>thunk</button>
        <Header />
        <Main />
        <GlobalStyle />
      </MainAppWrapper>

    </AppWrapper>
  );
};

export default App;
