import axios from "axios";

const API_KEY = 'd6487c851d4294ca86263fcb2861f71f';

class ApiService{
  static fetchWeather = async(value = 'Kyiv') => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=${API_KEY}`
    const response = await fetch(url, { method: 'GET'})
    const result = await response.json()
    console.log('result', result);
    return result
  }
}

export default ApiService;

const URL = 'https://openweathermap.org/data/2.5/weather'
const API_Key = 'd6487c851d4294ca86263fcb2861f71f';

const fetchWeather = async (query) => {
  const responce = await axios.get(URL, {
    params: {
      q: query,
      units: 'metric',
      APPID: API_Key,
    }
  })
}
