import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import SearchInput from './components/searchInput/SearchInput';
import WeatherItems from './components/weatherList/WeatherItems';

import { fetchWeather } from '../service/ApiService';

import { MainWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {

  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = async (e) => {
    if (e.key === 'Enter') {
      const data = await fetchWeather(query);
      setWeather(data);
      setQuery('');
    }
  };

  useEffect(() => {
    dispatch(fetchWeather)
  }, [])

  return (
    <MainWrapper>
      <SearchInput query={query} setQuery={setQuery} search={search} />
      <WeatherItems weather={weather} />
      <GlobalStyle />
    </MainWrapper>
  );
};

export default App;
