import React, { useState } from 'react';

import SearchInput from './components/searchInput/SearchInput';
import WeatherItems from './components/weatherList/WeatherItems';

import { fetchWeather } from '../service/ApiService';

import { MainWrapper } from './styledComponents/App';
import GlobalStyle from '../styledComponents/GlobalStyle';

const App = () => {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = async (e) => {
    if (e.key === 'Enter') {
      const data = await fetchWeather(query);
      setWeather(data);
      setQuery('');
    }
  };
  return (
    <MainWrapper>
      <SearchInput query={query} setQuery={setQuery} search={search}  />
      <div>
        {weather.main && (
          <div>
            <h2>
              <span>{weather.name}</span>
              <sup>{weather.sys.country}</sup>
            </h2>
            <div>
              {Math.round(weather.main.temp)}
              <sup>&deg;C</sup>
            </div>
            <div>
              <img
                className="city-icon"
                src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
                alt={weather.weather[0].description}
              />
              <p>{weather.weather[0].description}</p>
            </div>
          </div>
        )}
      </div>
      <WeatherItems  />
      <GlobalStyle />
    </MainWrapper>
  );
};

export default App;
