import React from 'react';
import { useTranslation } from 'react-i18next';

import russiaFlagIcon from '../../../../assets/image/russiaFlagIcon.png'
import unitedKingdomFlagIcon from '../../../../assets/image/'
import { BtnWrapper } from './styledComponents/CustomButton';

const CustomButton = () => {
  const { i18n } = useTranslation();

  const changeLanguage = (language) => {
    i18n.changeLanguage(language);
  };

  return (
    <BtnWrapper>
      <img src={russiaFlagIcon} alt='russiaFlagIcon' onClick={() => changeLanguage('ru')}/>
      <button className='btn' onClick={() => changeLanguage('en')}>EN</button>
    </BtnWrapper>
  );
};

export default CustomButton;
