import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { languegeObject } from '../../../store/data/languageObject';

// import { fetchWeatherAction } from '../../../../store/redux/slices/weatherSlices';

import {
  MainDailyWeatherWrapper,
  DailyWeatherWrapper,
} from './styledComponents/DailyWeather';

const DailyWeather = () => {
  const lang = useSelector(state => state.app.lang)

  const weather = useSelector(state => state.app.weather);
  // useEffect(() => {
  //   dispatch(fetchWeatherAction('Kyiv'));
  // }, [dispatch]);


  return (
    <MainDailyWeatherWrapper>
      {/* {isLoading ? (
        <h2>{t('mainePageWeather.loading')}</h2>
      ) : error ? (
        <h3>{error?.message}</h3>
      ) : ( */}
        <DailyWeatherWrapper>
          <div>
            <div className="dailyWeatherCardInfo">
              <h1>
                {Math.floor(weather?.main.temp)}
                <sup>&deg;</sup>
              </h1>
              <img
                src={`https://openweathermap.org/img/wn/${weather?.weather[0].icon}@2x.png`}
                alt={weather?.weather[0].description}
              />
            </div>
            <div className="dailyWeatherCardInfo">
              <h3>{languegeObject[lang].mainePageWeather.today}</h3>
            </div>
          </div>
          <div className="dailyWeatherItem">
            <p>{languegeObject[lang].mainePageWeather.city}:</p>
            <span>{weather?.name}</span>
            <sup>{weather?.sys.country}</sup>
          </div>
        </DailyWeatherWrapper>
      {/* )} */}
    </MainDailyWeatherWrapper>
  );
};

export default DailyWeather;
