import {
  APP_CHANGE_LANG,
  FETCH_DATA_ERROR,
  FETCH_DATA_START,
  FETCH_DATA_SUCCESS,
} from '../actionType';

import ApiWeather from '../Api';

const fetchDataStart = () => {
  return { type: FETCH_DATA_START };
};

const fetchDataSuccess = (payload) => ({ type: FETCH_DATA_SUCCESS, payload });
const fetchDataError = () => ({ type: FETCH_DATA_ERROR });

export const changeLang = (payload) => ({ type: APP_CHANGE_LANG, payload });

export const fetchData = (city, ln) => {
  return async (dispatch) => {
    try {
      dispatch(fetchDataStart());
      const data = await ApiWeather.fetchData(city, ln);
      dispatch(fetchDataSuccess(data));
    } catch (e) {
      dispatch(fetchDataError());
    }
  };
};
